import Daemon from './main'

const daemon = new Daemon({sudoPasswd: 'my_sudo_pass'})

daemon.isRunning().then(res => {
  if (res) {
    console.log('daemon is running')
    console.log('kill it')
    daemon.kill()
  } else {
    console.log('daemon is not running')
    console.log('start it')
    daemon.start()
  }
})
